wine:produces rdf:type owl:ObjectProperty ;
    owl:inverseOf wine:producedBy .

wine:hasCharacteristic rdf:type owl:ObjectProperty ;
    owl:domain wine:GrapeVariety ;
    owl:range wine:Characteristic .

wine:hasFlavorProfile rdf:type owl:ObjectProperty ;
    owl:domain wine:GrapeVariety ;
    owl:range wine:FlavorProfile .

wine:undergoesAgingProcess rdf:type owl:ObjectProperty ;
    owl:inverseOf wine:agingProcessOf .

wine:producedBy rdf:type owl:ObjectProperty ;
    owl:inverseOf wine:produces .

wine:usesProductionMethod rdf:type owl:ObjectProperty ;
    owl:domain wine:Wine ;
    owl:range wine:ProductionMethod .

wine:hasAttribute rdf:type owl:ObjectProperty ;
    owl:domain wine:Wine ;
    owl:range wine:Attribute .

wine:hasAward rdf:type owl:ObjectProperty ;
    owl:domain wine:Wine ;
    owl:range wine:Competition .

wine:hasVintage rdf:type owl:ObjectProperty ;
    owl:domain wine:WineRegion ;
    owl:range wine:Vintage .

wine:meetsDietaryRestriction rdf:type owl:ObjectProperty ;
    owl:domain wine:Wine ;
    owl:range wine:DietaryRestriction .

wine:hasStorageCondition rdf:type owl:ObjectProperty ;
    owl:domain wine:Wine ;
    owl:range wine:StorageCondition .

wine:requiresTemperature rdf:type owl:ObjectProperty ;
    owl:domain wine:Wine ;
    owl:range wine:Temperature .

wine:providesInformation rdf:type owl:ObjectProperty ;
    owl:domain wine:WineLabel ;
    owl:range wine:Information .

wine:hasDescriptor rdf:type owl:ObjectProperty ;
    owl:domain wine:TastingNote ;
    owl:range wine:Descriptor .

wine:hasQualityFactor rdf:type owl:ObjectProperty ;
    owl:domain wine:Wine ;
    owl:range wine:QualityFactor .

wine:hasEvent rdf:type owl:ObjectProperty ;
    owl:domain wine:WineRegion ;
    owl:range wine:Event .

wine:hasRegulation rdf:type owl:ObjectProperty ;
    owl:domain wine:WineProduction ;
    owl:range wine:Regulation .

wine:hasCertification rdf:type owl:ObjectProperty ;
    owl:domain wine:WineProduction ;
    owl:range wine:Certification .

wine:produces owl:inverseOf wine:producedBy .

wine:agingProcessOf owl:inverseOf wine:undergoesAgingProcess .

